                AVLNode *temp = node;
                node - node->left;
                delete temp;
            }
        else
        {
            AVLNode *temp = findMax(node->left);
            node->data = temp->data;
            node->left = deleteNode(node->left, temp->data);
        }
        }
    }

    if (node == NULL)
    {
        return node;
    }

    /* 2. Update height of node */
    node->height = 1 + max(height(node->left), height(node->right));


    /* 3. Get the balance factor of this node to check
    whether this node became unbalanced */
    int balance = getBalance(node);

    // Left Heavy imbalance
    if (balance > 1)
    {
        if (getBalance(node->left) >= 0)
        {
            // Left Left Case
            return RRrotation(node);
        }
        else if (getBalance(node->left) < 0)
        {
            // Left Right Case
            node->left = LLrotation(node->left);
            return RRrotation(node);
        }
    }
    // Right Heavy imbalance
    if (balance < -1)
    {
        if (getBalance(node->right) <= 0)
        {
            // Right Right Case
            return LLrotation(node);
        }
        else if (getBalance(node->right) > 0)
        {
            // Right Left Case
            node->right = RRrotation(node->right);
            return LLrotation(node);
        }
    }

    return node;
}


/** @brief	Finds and returns the Lowest Common Ancestor (LCA) of two nodes in a binary search tree (BST).
 *  @param  root A pointer to the root node of the BST.
 *	@param  node1 The value of the first node for which the LCA is to be found.
 *	@param  node2 The value of the second node for which the LCA is to be found.
 *	@return A pointer to the node that is the Lowest Common Ancestor (LCA) of node1 and node2 in the BST.
 *          If no common ancestor is found, it returns NULL.
 */
AVLNode *findLCA(AVLNode *root, int node1, int node2)
{
    if (root == NULL)
    {
        return NULL;
    }

    if (root->data > node1 && root->data > node2)
    {
        return findLCA(root->left, node1, node2);
    }

    if (root->data < node1 && root->data < node2)
    {
        return findLCA(root->right, node1, node2);
    }

    return root;
}

/** @brief	Find the distance between two nodes in the BST.
 *  @param  root The root of the BST.
 *	@param  node1 The value of the first node.
 *	@param  node2 The value of the second node.
 *  @return The distance between the two nodes if found, otherwise -1.
 */
int findDistanceBetweenNodes(AVLNode *root, int node1, int node2)
{
    if (root == NULL)
    {
        return -1;
    }

    // Find the LCA (Lowest Common Ancestor) of node1 and node2
    AVLNode *lca = findLCA(root, node1, node2);

    if (lca == NULL)
    {
        return -1;
    }

    // Calculate the distances from the LCA to node1 and node2
    int distance1 = 0;
    int distance2 = 0;

    AVLNode *current = lca;
    while (current->data != node1)
    {
        if (node1 < current->data)
        {
            current = current->left;
        }
        else
        {
            current = current->right;
        }
        distance1++;
    }

    current = lca;
    while (current->data != node2)
    {
        if (node2 < current->data)
        {
            current = current->left;
        }
        else
        {
            current = current->right;
        }
        distance2++;
    }

    // Return the total distance between node1 and node2
    return distance1 + distance2;
}

/** @brief	Traverses the binary search tree in preorder and saves the values
