#include <iostream>

using namespace std;

/*
 * The data structure of a binary search tree node
 */
struct AVLNode
{
    int data;
    AVLNode *left = NULL;  // Initialized to NULL
    AVLNode *right = NULL; // Initialized to NULL
    int height;
    int balFactor;
};

/** @brief	Creates a new binary search tree (BST) node with the given value.
 *  @param  value The integer value to be stored in the new node.
 *	@return A pointer to the newly created BST node with initialized data and children set to NULL.
 */
AVLNode *createNode(int value)
{
    AVLNode *newNode = new AVLNode;
    newNode->data = value;
    newNode->left = newNode->right = NULL;
    newNode->height = 0;
    return newNode;
}

int height(AVLNode *node)
{
    if (node == NULL)
    {
        return -1;
    }
    return node->height;
}

int max(int a, int b)
{
    return (a > b) ? a : b;
}

AVLNode *LLrotation(AVLNode *y)
{
    AVLNode *x = y->left;
    AVLNode *T2 = x->right;

    x->right = y;
    y->left = T2;

    y->height = max(height(y->left), height(y->right)) + 1;
    x->height = max(height(x->left), height(x->right)) + 1;

    return x;
}

AVLNode *RRrotation(AVLNode *x)
{
    AVLNode *y = x->right;
    AVLNode *T2 = y->left;

    y->left = x;
    x->right = T2;

    x->height = max(height(x->left), height(x->right)) + 1;
    y->height = max(height(y->left), height(y->right)) + 1;

    return y;
}

int getBalance(AVLNode *node)
{
    if (node == NULL)
    {
        return 0;
    }
    return height(node->left) - height(node->right);
}

/** @brief	Inserts a value into a binary search tree.
 *  @param  root The Node the value will be inserted into.
 *	@param  value The int to be inserted into the tree.
 *	@return pointer to the new node.
 */
AVLNode *insertNode(AVLNode *node, int value)
{
    /* 1. Perform the normal BST insertion */
    if (node == NULL)
    {
        return createNode(value);
    }

    if (value < node->data)
    {
        node->left = insertNode(node->left, value);
    }
    else if (value > node->data)
    {
        node->right = insertNode(node->right, value);
    }

    /* 2. Update height of this ancestor node */
    node->height = 1 + max(height(node->left), height(node->right));


    /* 3. Get the balance factor of this ancestor node to check
    whether this node became unbalanced */
    int balance = getBalance(node);
    node->balFactor = getBalance(node);

    if (balance > 1)
    {
        if (value < node->left->data)
        {
            // Left Left Case
            return LLrotation(node);
        }
        else
        {
            // Left Right Case
            node->left = RRrotation(node->left);
            return LLrotation(node);
        }
    }
    if (balance < -1) {
        if (value > node->right->data) {
            // Right Right Case
            return RRrotation(node);
        } else {
            // Right Left Case
            node->right = LLrotation(node->right);
            return RRrotation(node);
        }
    }

    node->balFactor = getBalance(node);
    return node;
}

/** @brief	Searches a binary search tree for a value.
 *  @param	root The Node to be searched.
 *	@param	value The int to be searched for.
 *  @return	The Node containing the key if it exists.
 */
bool searchData(AVLNode *root, int value)
{
    if (root == NULL)
    {
        return false;
    }
    if (root->data == value)
    {
        return true;
    }
    if (value < root->data)
    {
        return searchData(root->left, value);
    }
    else
    {
        return searchData(root->right, value);
    }
}

/** @brief	Searches a binary search tree for a value and returns the corresponding node.
 *  @param	root The Node to be searched.
 *	@param	value The int to be searched for.
 *  @return	The Node containing the key if it exists, otherwise NULL.
 */
AVLNode *search(AVLNode *root, int value)
{
    if (root == 0 || root->data == value)
    {
        return root;
    }
    else if (value < root->data)
    {
        return search(root->left, value);
    }
    else
    {
        return search(root->right, value);
    }
    return NULL;
}

/** @brief	Finds and returns the height of a binary search tree (BST).
 *  @param  root A pointer to the root node of the BST.
 *	@return The height of the BST, where the height of an empty tree is -1 and the height of a tree with a single node is 0.
 */
int height(AVLNode *root)
{
    if (root == NULL)
    {
        return -1;
    }

    return root->height;
}

/** @brief	Finds and returns the minimum node value in a binary search tree (BST).
 *  @param  root A pointer to the root node of the BST.
 *	@return A pointer to the node containing the minimum value in the BST, or nullptr if the tree is empty.
 */
AVLNode *findMin(AVLNode *root)
{
    if (root == 0 || root->left == 0)
    {
        return root;
    }
    return findMin(root->left);
}

/** @brief	Finds and returns the maximum node value in a binary search tree (BST).
 *  @param  root A pointer to the root node of the BST.
 *	@return A pointer to the node containing the maximum value in the BST, or nullptr if the tree is empty.
 */
AVLNode *findMax(AVLNode *root)
{
    if (root == 0 || root->right == 0)
    {
        return root;
    }
    return findMax(root->right);
}

/** @brief	Finds the in-order successor of a node in the BST.
 *  @param  root The root of the BST.
 *	@param  data The value of the node whose successor is to be found.
 *  @return The in-order successor node if found, otherwise NULL.
 */
AVLNode *findInOrderSuccessor(AVLNode *root, int data)
{
    // Initialize the successor to NULL
    AVLNode *successor = NULL;

    while (root != NULL)
    {
        if (data < root->data)
        {
            // If data is less than the current node's data, update successor and move to the left subtree
            successor = root;
            root = root->left;
        }
        else if (data > root->data)
        {
            // If data is greater than the current node's data, move to the right subtree
            root = root->right;
        }
        else
        {
            // If data matches the current node's data, we have found the node
            // Case 1: Node has a right subtree, so find the minimum node in the right subtree
            if (root->right != NULL)
            {
                successor = findMin(root->right);
            }
            // Case 2: Node doesn't have a right subtree, so the successor is the ancestor that has a left child
            break;
        }
    }

    return successor;
}

/** @brief	Removes a value from a binary search tree.
 *  @param	root Node to remove the value from.
 *	@param	value The int to be removed from the tree.
 *	@return pointer to the new node.
 */
AVLNode *deleteNode(AVLNode *node, int value)
{
    if (node == NULL)
    {
        return node;
    }
    else
    {

        if (value < node->data)
        {
            node->left = deleteNode(node->left, value);
        }
        else if (value > node->data)
        {
            node->right = deleteNode(node->right, value);
        }
        else
        {
            if (node->left == NULL&& node->right==NULL)
            {
                delete node;
                node = NULL;
            }
            else if (node->left == NULL)
            {
                AVLNode *temp = node;
                node = node->right;
                delete temp;
            }
            else if (node->right == NULL)
            {
                AVLNode *temp = node;
                node = node->left;
                delete temp;
            }
        else
        {
            AVLNode *temp = findMax(node->left);
            node->data = temp->data;
            node->left = deleteNode(node->left, temp->data);
        }
        }
    }

    if (node == NULL)
    {
        return node;
    }

    /* 2. Update height of node */
    node->height = 1 + max(height(node->left), height(node->right));


    /* 3. Get the balance factor of this node to check
    whether this node became unbalanced */
    int balance = getBalance(node);
    node->balFactor = getBalance(node);

    // Left Heavy imbalance
    if (balance > 1)
    {
        if (getBalance(node->left) >= 0)
        {
            // Left Left Case
            return RRrotation(node);
        }
        else if (getBalance(node->left) < 0)
        {
            // Left Right Case
            node->left = LLrotation(node->left);
            return RRrotation(node);
        }
    }
    // Right Heavy imbalance
    if (balance < -1)
    {
        if (getBalance(node->right) <= 0)
        {
            // Right Right Case
            return LLrotation(node);
        }
        else if (getBalance(node->right) > 0)
        {
            // Right Left Case
            node->right = RRrotation(node->right);
            return LLrotation(node);
        }
    }

    node->balFactor = getBalance(node);
    return node;
}


/** @brief	Finds and returns the Lowest Common Ancestor (LCA) of two nodes in a binary search tree (BST).
 *  @param  root A pointer to the root node of the BST.
 *	@param  node1 The value of the first node for which the LCA is to be found.
 *	@param  node2 The value of the second node for which the LCA is to be found.
 *	@return A pointer to the node that is the Lowest Common Ancestor (LCA) of node1 and node2 in the BST.
 *          If no common ancestor is found, it returns NULL.
 */
AVLNode *findLCA(AVLNode *root, int node1, int node2)
{
    if (root == NULL)
    {
        return NULL;
    }

    if (root->data > node1 && root->data > node2)
    {
        return findLCA(root->left, node1, node2);
    }

    if (root->data < node1 && root->data < node2)
    {
        return findLCA(root->right, node1, node2);
    }

    return root;
}

/** @brief	Find the distance between two nodes in the BST.
 *  @param  root The root of the BST.
 *	@param  node1 The value of the first node.
 *	@param  node2 The value of the second node.
 *  @return The distance between the two nodes if found, otherwise -1.
 */
int findDistanceBetweenNodes(AVLNode *root, int node1, int node2)
{
    if (root == NULL)
    {
        return -1;
    }

    // Find the LCA (Lowest Common Ancestor) of node1 and node2
    AVLNode *lca = findLCA(root, node1, node2);

    if (lca == NULL)
    {
        return -1;
    }

    // Calculate the distances from the LCA to node1 and node2
    int distance1 = 0;
    int distance2 = 0;

    AVLNode *current = lca;
    while (current->data != node1)
    {
        if (node1 < current->data)
        {
            current = current->left;
        }
        else
        {
            current = current->right;
        }
        distance1++;
    }

    current = lca;
    while (current->data != node2)
    {
        if (node2 < current->data)
        {
            current = current->left;
        }
        else
        {
            current = current->right;
        }
        distance2++;
    }

    // Return the total distance between node1 and node2
    return distance1 + distance2;
}

