#include <string.h>
#include <stdlib.h>
#include <stdio.h>
#include <math.h>


#define DECIMAL_MAX_DIGITS 10				//Maximum number of digits allowed in a decimal number, excluding the sign 
#define DECIMAL_MIN_VALUE -2147483648		//Minimum value allowed to a decimal number
#define DECIMAL_MAX_VALUE 2147483647		//Maximum value allowed to a decimal number

#define BINARY_WORD_SIZE 32					//Number of digits of a binary word

#define BIASED_VALUE 127					//Bias value for biased_127 number
#define BIASED_127_DECIMAL_MAX_DIGITS 3		//Maximum number of digits allowed in a biased_127 decimal number 
#define BIASED_127_BINARY_MAX_DIGITS 8 		//Maximum number of digits allowed in a biased_127 binary number 
#define BIASED_127_DECIMAL_MIN_VALUE -126	//Minium decimal value allowed to a valid biased_127 exponent in a 
											//IEE 754 32-bit floating point number is -126
#define BIASED_127_DECIMAL_MAX_VALUE 127	//Maxinum decimal value allowed to a valid biased_127 exponent in a 
											//IEE 754 32-bit floating point number is 127

#define REAL_PART_MAX_DIGITS 10 			//Maximum number of digits allowed in a part of a real number 
#define REAL_MAX_DIGITS 22					//Maximum number of digits allowed in a real number, including the
											//decimal point and the sing.


#define FLOATING_POINT_EXPONENT_DIGITS 8 	//Maximum number of binary digits allowed in an exponent 
					 						//of a floating point number 
#define FLOATING_POINT_SIGNIFICAND_START 9 	//Bit index in a floating point number where the significand
						  					//part of the floating point number starts
#define FLOATING_POINT_SIGNIFICAND_DIGITS 23 //Maximum number of binary digits allowed in a significand 
						  					// of a floating point number 
#define FLOATING_POINT_MAX_DIGITS 32 		//Maximum number of binary digits allowed in a floating point number 

#define TRUE 1								//Logical TRUE representation
#define FALSE 0								//Logical FALSE representation

#define ERROR_MESSAGE_FORMAT "\n\tinfo: %s is not a valid %s\n"



/**
 * @brief Reverses the order of non-NULL characters in a null-terminated string 
 * in place.
 * @param str Pointer to a null-terminated string to be reversed.
 */
void reverse(char* str){
	if (str == NULL || strlen(str) == 0) {
		return;
	}
	int length = strlen(str);
	int start = 0;
	int end = length - 1;

	while (start < end) {
		char temp = str[start];
		str[start] = str[end];
		str[end] = temp;

		start++;
		end--;
	}
}
//Parameter 'str' is a NULL terminted string.
//Reverse all the non-NULL characters of the string 'str'


/**
 * @brief Extends an integer represented as a string to a specified word size 
 * with leading zeros.
 * @param number Pointer to a null-terminated string representing the integer 
 * to be extended.
 * @param word_size The desired word size for the extended integer.
 * @return Pointer to the extended integer string, or NULL if the input is 
 * invalid.
 */
char* extend_integer_binary(const char* number, int word_size) {

	if (number == NULL)  {
		return NULL;
	}

	if (strlen(number) > word_size) {
		char* extended_str = (char*)malloc(word_size + 1);
		int overflow = strlen(number) - word_size;
		for (int i = 0; i < word_size; i++) {
			extended_str[i] = number[i + overflow];
		}
		return extended_str;        
	}

	if (strlen(number) < word_size) {
		char* extended_str = (char*)malloc(word_size + 1);
		int num_zeros = word_size - strlen(number);
		memset(extended_str, '0', num_zeros);
		strcpy(extended_str + num_zeros, number);
		return extended_str;       
	}

	if (strlen(number) == word_size) {
		char* extended_str = (char*)malloc(word_size + 1);
		strcpy(extended_str, number);
		return extended_str;       
	}

}
//Allocate enough memory for an extended number.
//If the number of digits in 'number' is more than 'word_size', copy
//'word_size' least significant digits from the number.
//If the number of digits in 'number' is less than 'word_size' do the followings:
//	Fill the most significant (left most) extra digits of the extended number with zeros 
//	Copy the least significant (right most) digits from the number.
//If the number of digits in 'number' is equal to 'word_size', copy all the digits from
//'number'.
//Return the pointer of the extended number.


/**
 * @brief Extends a binary fraction represented as a string to a specified word 
 * size with trailing zeros.
 * @param number Pointer to a null-terminated string representing the binary 
 * fraction to be extended.
 * @param word_size The desired word size for the extended binary fraction.
 * @return Pointer to the extended binary fraction string, or NULL if the input 
 * is invalid.
 */
char* extend_fraction_binary(const char* number, int word_size)	{

	if (number == NULL)	{
		return NULL;
	}

	if (strlen(number) > word_size)	{
		char* extended_str = (char*)malloc(word_size + 1);
		for (int i = 0; i < word_size; i++) {
			extended_str[i] = number[i];
		}
		return extended_str;        
	}

	if (strlen(number) < word_size)	{
		char* extended_str = (char*)malloc(word_size + 1);
		int trailing_zeros = word_size - strlen(number);
		strcpy(extended_str, number);
		memset(extended_str + strlen(number), '0', trailing_zeros);
		return extended_str;      
	}

	if (strlen(number) == word_size)	{
		char* extended_str = (char*)malloc(word_size + 1);
		strcpy(extended_str, number);
		return extended_str;       
	}

}
//Allocate enough memory for an extended number.
//If the number of digits in 'number' is more than 'word_size', copy
//'word_size' most significant digits from the number.
//If the number of digits in 'number' is less than 'word_size' do the followings:
//	Copy the most significant (left most) digits of the extended number from the number.
//	Fill the least significant (right most) extra digits of the extended number with zeros.
//If the number of digits in 'number' is equal to 'word_size', copy all the digits from
