/**
 * @brief Converts an integer decimal string to a binary string representation 
 * with a specified word size.
 * @param number    Pointer to a string representing the decimal integer.
 * @param word_size The desired word size (number of binary digits) for the 
 * binary representation.
 * @return Pointer to a dynamically allocated string representing the binary 
 * number with the specified word size, or NULL if memory allocation fails or 
 * an error occurs.
 */
char* integer_to_binary(long int_num, int word_size){

	if (int_num == 0)	{
    	return strdup("0");
	}

    char* binary_str = (char*)calloc(word_size + 1, (sizeof(char)));
    if (binary_str == NULL) {
        free(binary_str);
        return NULL;
	}

    int i = word_size - 1, index = 0;
    while (i >= 0) {
        char bit = (int_num & (1L << i)) ? '1' : '0'; // Check the i-th bit
        if (bit == '1' || index > 0) {
            binary_str[index++] = bit; // Include non-leading zeros
        }
        i--;
    }

    return binary_str;

}
//Dynamically allocate enough memory for a new string so that it can hold a binary
//number with 'word_size' binary digits plus a terminating NULL character.
//Use decimal-to-binary conversion algorithm to get the equivalent binary digits of 
//the decimal number int_num' and save the binary digits into the new string.
//Return the pointer of the new string that represents the converted binary number.


char* fraction_to_binary(const char* number, int word_size){
	if (number == NULL)	{
		return NULL;
	}

    char* binary_str = (char*)calloc(word_size + 1, (sizeof(char)));
    if (binary_str == NULL) {
        free(binary_str);
        return NULL;
    }

    double product = atoi(number);

    product = product/pow(10, strlen(number));

	int i = 0;
	do	{
        product = product *2;

        if (product >= 1)    {
            binary_str[i] = '1';
            product = product - 1;
        } else {
            binary_str[i] = '0';
        }
		i++;
    } while(i < word_size && product != 0);

    return binary_str;

}
//Dynamically allocate enough memory for a new string so that it can hold a binary
//number with 'word_size' binary digits.
//Use decimal-fraction-to-binary conversion algorithm to get the equivalent binary digits of 
//the decimal fraction number and save the binary digits into the new string.
//Return the pointer of the new string that represents the converted binary number.

long unsigned_binary_to_integer(const char* number){

    long decimalValue = 0;
    int binaryLength = strlen(number);
    for (int i = 0; i < binaryLength; i++) {
        decimalValue += (number[i] - '0') * pow(2, binaryLength - i - 1);
    }

    return decimalValue;
}
//Assume the most significant digit as the non-sign bit.
//Use binary-to-decimal conversion algorithm to get the equivalent 
//decimal (integer) number from the binary digits.
//Return the integer number.

int signed_binary_to_integer(const char* number){

    int decimalValue = 0;
    int binaryLength = strlen(number);
    for (int i = 0; i < binaryLength; i++) {
        decimalValue += (number[i] - '0') * pow(2, binaryLength - i - 1);
    }

    return decimalValue;
}
//Assume the most significant digit as the sign bit.
//Use binary-to-deciaml conversion algorithm to get the equivalent 
//decimal (integer) number from the binary digits.
//Return the integer number.


char one_bit_add(char op1, char op2, char cin, char* cout){
	int temp = (op1 - '0') + (op2 - '0') + (cin - '0');

	switch(temp) {
	case 0:
		cout[0] = '0';
		return '0';
		break;
	case 1:
		cout[0] = '0';
		return '1';
		break;	
	case 2:
		cout[0] = '1';
		return '0';
		break;
	case 3:
		cout[0] = '1';
		return '1';
		break;
	default:
		cout[0] = '0';
		return '0';
	}

	return 0;
}
//Parameters 'op1', 'op2' and 'cin' are binary operands, their value can be either
//'0' or '1'.
//Add these binary operands and save the resultant carry of the addition into 'cout'.
//Return the result of the addition to the caller.


char* get_magnitude_binary(const char* signed_number, int word_size){
    char* result = (char*)malloc(strlen(signed_number) + 1);
    if (signed_number[0] == '-' || signed_number[0] == '+') {
        strcpy(result, signed_number + 1);
    } else {
        strcpy(result, signed_number);
    }

    long number = atol(result);
    free(result);
    return integer_to_binary(number, word_size);
}
//Ignore the sign bit if there is one and get the integer value of the 'signed_number'.
//Call the function integer_to_binary() and return the result.


char* to_sign_magnitude(const char* number){
    if (is_decimal(number)) {
        char* binary_magnitude = get_magnitude_binary(number, BINARY_WORD_SIZE);
        char* extended_sign_magnitude = extend_integer_binary(binary_magnitude, BINARY_WORD_SIZE);
        free(binary_magnitude);

    if (number[0] == '-') {
        extended_sign_magnitude[0] = '1';
    }
    if (number[0] == '+') {
        extended_sign_magnitude[0] = '0';
    }

    return extended_sign_magnitude;
    }

    return NULL;
}
//Check whether the number is a valid decimal number by calling is_decimal() function.
//If yes, perform the followings:
//	Call get_magnitude_binary() function to get the binary representation of the
//	magnitude of the number.
//	Extend the binary representation of the magnitude to BINARY_WORD_SIZE by
//	calling extend_integer_binary_to_word_size() function.
//	Free all dynamically allocated memories that are no longer required.
//	Set the appropriate sign bit for the signed magnitude number and return it
//	to the caller.


char* from_sign_magnitude(const char* number){
    if (!is_binary(number)) {
        return NULL;
    }

    long integer = strtol(number + 1, NULL, 2);
    // long integer = unsigned_binary_to_integer(number + 1);


    // Create a buffer to hold the resulting string
    char* result = (char*)malloc(DECIMAL_MAX_DIGITS + 3);
    if (result == NULL) {
        return NULL;
    }

    if (number[0] == '1') {
        sprintf(result, "-%ld", integer);
    } else {
        sprintf(result, "+%ld", integer);
    }

    return result;
}
//Check whether the number is a valid binary number by calling is_binary() function.
//If yes, perform the followings:
//      Call unsigned_binary_to_integer() function skipping the sign-bit from 'number'
//	and get the integer equivalent of the sign magnitude number.
//	Convert the integer into a new string and add the appropriate sign to the string.
//	Return the string.


void ones_complement(char* number){

    if (number == 0)    {
        return;
    }
    int i = 0;
    do{
        if(number[i] == '0')    {
            number[i] = '1';
        } else {
            number[i] = '0';
        }
        i++;
    } while(i < strlen(number));
	return;
}
//Flip each binary digit in the number.


void twos_complement(char* number){

    char* answer = (char*)malloc(strlen(number) + 1);
    strcpy(answer, number);
    ones_complement(answer);

    int carry = 1;
    int i = strlen(answer) - 1;

    while (i >= 0 && carry != 0) {
        char result;
        answer[i] = one_bit_add(answer[i], '0', carry + '0', &result);
        carry = result - '0';
        i--;
    }

    strcpy(number, answer);

    free(answer);
}
