char* to_twos_complement(const char* number){
    // Check if the input is a valid decimal number
    if (!is_decimal(number)) {
        return NULL; // Invalid input
    }

    // Get the magnitude binary representation
    char* binary_magnitude = get_magnitude_binary(number, BINARY_WORD_SIZE);

    // Extend the binary representation to BINARY_WORD_SIZE
    char* extended_magnitude = extend_integer_binary(binary_magnitude, BINARY_WORD_SIZE);
    free(binary_magnitude);

    // Check the sign bit
    if (number[0] == '-') {
        // If negative, get the two's complement
        twos_complement(extended_magnitude);
    }

    return extended_magnitude;
}
