char* int_to_biased_127(int number){

    if (number < BIASED_127_DECIMAL_MIN_VALUE || number > BIASED_127_DECIMAL_MAX_VALUE) {
        return NULL;
    }

    int biased_number = number + BIASED_VALUE;

    char* binary_representation = integer_to_binary(biased_number, BIASED_127_BINARY_MAX_DIGITS);

    char* extended_binary_representation = extend_integer_binary(binary_representation, BIASED_127_BINARY_MAX_DIGITS);

