    // Check whether the number is a valid real number
    if (!is_real(number)) {
        return NULL;
    }

    // Extract integer and fractional parts
    char* integer_part = get_integer_part(number);
    char* fraction_part = get_fraction_part(number);

    // Get binary representations of integer and fractional parts
    char* integer_binary = get_magnitude_binary(integer_part, BINARY_WORD_SIZE);
    char* fraction_binary = fraction_to_binary(fraction_part, BINARY_WORD_SIZE);

    // Extend the integer and fractional binaries to BINARY_WORD_SIZE
    char* extended_integer_binary = extend_integer_binary(integer_binary, BINARY_WORD_SIZE);
    char* extended_fraction_binary = extend_fraction_binary(fraction_binary, BINARY_WORD_SIZE);

    // Normalize the integer and fractional binaries and adjust the exponent
    int exponent = 0;
    normalize(extended_integer_binary, extended_fraction_binary, &exponent);

    // Get the binary representation of the adjusted exponent
    char* exponent_binary = int_to_biased_127(exponent);

    // Determine the sign bit
    char sign_bit = '0'; // Assume positive

    if (number[0] == '-') {
        sign_bit = '1'; // Set sign bit to 1 for negative numbers
    }

    // Allocate memory for the floating point string
    int total_size = BINARY_WORD_SIZE + BINARY_WORD_SIZE + 1;
    char* floating_point = (char*)malloc(total_size);

    // Construct the floating point representation
    snprintf(floating_point, total_size, "%c%s%s", sign_bit, exponent_binary, extended_fraction_binary);

    // Free dynamically allocated memory
    free(integer_part);
    free(fraction_part);
    free(integer_binary);
    free(fraction_binary);
    free(extended_integer_binary);
    free(extended_fraction_binary);
    free(exponent_binary);

    return floating_point;
}
