void normalize(char* integer, char* fraction, int* exponent){

    char* integer_result = (char*)malloc(strlen(integer) + 1);
    strcpy(integer_result, integer);
    char* fraction_result = (char*)malloc(strlen(fraction) + 1);
    strcpy(fraction_result, fraction);


    char* first_one = strchr(integer_result, '1');

    int index = 0;
    int fraction_one = 0;

    if (first_one == NULL)  {
        first_one = strchr(fraction, '1');
        if (first_one == NULL)  {
            return;
        }
        // Find the index of the first '1' in the fraction part
        for (int i = 0; i < strlen(fraction); i++) {
            if (fraction[i] == '1') {
                index = i;
                fraction_one = 1;
                break;
            }
        }
    } else  {
        // Find the index of the first '1' in the integer part
        for (int i = 0; i < strlen(integer_result); i++) {
            if (integer_result[i] == '1') {
                index = i;
                break;
            }
        }
    }

    int shift = 0;
    if (fraction_one == 0) {
        shift = (strlen(integer) - index - 1);
    } else {
        shift = ((index + 1) * -1);
    }



    if (shift == 0)   {
        return;        
    }

    if (shift > 0)   {
        // Fill the first 'shift' digits of integer with leading 0s
        for (int i = 0; i < shift; i++) {
            integer_result[i] = '0';
        }
        // Copy the first 'shift' digits (including leading 0s and the first '1') from integer to integer_result
        for (int i = 0; i < shift; i++) {
            integer_result[i + shift] = integer[i];
        }

        // Copy the remaining part of the integer into fraction_result
        for (int i = 0; i < strlen(integer) - shift; i++) {
            fraction_result[i] = integer[i + shift];
        }
        // If the integer part is exhausted, copy from the fractional part
        for (int i = 0; i < strlen(integer) - shift; i++) { 
            fraction_result[i + shift] = fraction[i];
        }
    }

    if (shift < 0)   {
        // Shift the integer part
        for (int i = 0; i < strlen(integer) - (index + 1); i++) {
            integer_result[i] = integer[i + (index + 1)];
        }

        // Copy the fractional part into the integer part
        for (int i = 0; i < (strlen(fraction) + shift); i++) {
            integer_result[strlen(integer) - (index + 1) + i] = fraction[i];
        }

        // Copy the remaining fractional part into the fraction_result
        for (int i = 0; i < (strlen(fraction) + shift); i++) {
            fraction_result[i] = fraction[i - shift];
        }

        // // Extend fraction_result to BINARY_WORD_SIZE if needed
        // if (strlen(fraction_result) < BINARY_WORD_SIZE) {
        //     fraction_result = extend_fraction_binary(fraction_result, BINARY_WORD_SIZE);
        // }
    }

    strcpy(integer, integer_result);
    strcpy(fraction, fraction_result);
    *exponent = shift;

    free(integer_result);
    free(fraction_result);

