Help me analyze the time complexity of QuickSort:


/*
 * Chooses a pivot point based on median between first, mid and last and swaps it into first array posn.
 * Input: array, first, last.
 * Returns: void, sets pivot based on median of first, mid or last and swaps it into a[first].
 */
void choosePivot (int a[], int first, int last) {
	int mid = (first + last)/2;
	if ((a[first] <= a[mid]) && (a[mid] <= a[last])) swap(a[mid], a[first]);  	// first mid last
    if ((a[first] <= a[last]) && (a[last] <= a[mid])) swap(a[last], a[first]);  // first last mid
    if ((a[mid] <= a[first]) && (a[first] <= a[last])); 						// mid first last
    if ((a[mid] <= a[last]) && (a[last] <= a[first])) swap(a[last], a[first]);  // mid last first
    if ((a[last] <= a[first]) && (a[first] <= a[mid]));  						// last first mid
    swap(a[mid], a[first]);                            							// last mid first
}


/*
 * Partitions an array for quicksort.
 * Input: array, first posn, last posn and pivotIndex reference to pass back to quickSort.
 * Returns: void, array is partitioned.
 * Calls: choosePivot
 */
void partition(int theArray[], int first, int last, int &pivotIndex) {
	choosePivot(theArray, first, last);
	int pivot = theArray[first];
	int lastS1 = first;
	int firstUnknown = first + 1;
	for (; firstUnknown <= last; ++firstUnknown) {
		if (theArray[firstUnknown] < pivot) {
			++lastS1;
			swap(theArray[firstUnknown], theArray[lastS1]);
		}
	}
	swap(theArray[first], theArray[lastS1]);
	pivotIndex = lastS1;
}


/*
 * Sorts the items in an array into ascending order via divide-and-conquer.
 * Input: array, first, last.
 * Returns: void, array is sorted.
 * Calls: partition
 */
void quickSort(int theArray[], int first, int last) {
	int pivotIndex;
	if (first < last) {
		partition(theArray, first, last, pivotIndex);
		quickSort(theArray, first, pivotIndex-1);
		quickSort(theArray, pivotIndex+1, last);
	}
}

