```c
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define BINARY_WORD_SIZE 32

void normalize(char* integer, char* fraction, int* exponent) {
    int leadingOne = 0;

    // Find the index of the first '1' in the integer part
    for (int i = 0; i < BINARY_WORD_SIZE; i++) {
        if (integer[i] == '1') {
            leadingOne = i;
            break;
        }
    }

    // Calculate the shift amount for normalization
    int shiftAmount = BINARY_WORD_SIZE - 1 - leadingOne;

    // Shift the integer and fraction parts to the left
    for (int i = 0; i < BINARY_WORD_SIZE - shiftAmount; i++) {
        integer[i] = integer[i + shiftAmount];
    }
    for (int i = 0; i < BINARY_WORD_SIZE - shiftAmount; i++) {
        fraction[i] = fraction[i + shiftAmount];
    }

    // Update the exponent value
    *exponent -= shiftAmount;
}

