#include <math.h>

// Definitions for is_real, get_integer_part, get_fraction_part, get_magnitude_binary, fraction_to_binary,
// extend_integer_binary, extend_fraction_binary, normalize, int_to_biased_127 go here...

int is_real(const char* number);
int get_integer_part(const char* real);
long double get_fraction_part(const char* real);
char* get_magnitude_binary(const char* integerPart);
char* fraction_to_binary(long double fraction);
char* extend_integer_binary(const char* binary);
char* extend_fraction_binary(const char* binary);
void normalize(char* integer, char* fraction, int* exponent);
char* int_to_biased_127(int exponent);

char* to_floating_point(const char* number) {
    if (!is_real(number)) {
        // Not a valid real number, return an error or handle the error as needed
        return NULL; // For example, returning NULL to indicate an error
    }

    // Extract the integer and fraction parts
    long double realValue = strtold(number, NULL);
    long double integerPart = floorl(realValue);
    long double fractionPart = realValue - integerPart;

    // Convert the integer and fraction parts to binary
    char* integerBinary = get_magnitude_binary(number);
    char* fractionBinary = fraction_to_binary(fractionPart);

    // Extend the integer and fraction binaries to BINARY_WORD_SIZE
    integerBinary = extend_integer_binary(integerBinary);
    fractionBinary = extend_fraction_binary(fractionBinary);

    // Normalize the integer and fraction binaries and adjust the exponent
    int exponent = 0;
    normalize(integerBinary, fractionBinary, &exponent);

    // Convert the exponent to biased_127 binary representation
    char* exponentBinary = int_to_biased_127(exponent);

    // Allocate memory for the result string
    int bufferSize = 100; // Adjust this size as needed
    char* result = (char*)malloc(bufferSize);

    // Set the sign bit based on the sign of the real number
    char sign = (realValue < 0) ? '1' : '0';

    // Copy the sign, exponent, and significand into the floating point string
    snprintf(result, bufferSize, "%c%s%s", sign, exponentBinary, fractionBinary);

    return result;
}

int main() {
    const char* realNumber = "123.456"; // Example decimal real number

    if (is_real(realNumber)) {
        char* floatingPointNumber = to_floating_point(realNumber);
        printf("Floating-point number: %s\n", floatingPointNumber);

        // Don't forget to free the dynamically allocated memory when you're done with floatingPointNumber
        free(floatingPointNumber);
    } else {
        printf("Invalid real number.\n");
    }

    return 0;
}
