char* to_floating_point(const char* number) {
    if (!is_real(number)) {
        // Not a valid real number, return an error or handle the error as needed
        return NULL; // For example, returning NULL to indicate an error
    }

    // Extract integer and fraction parts
    char* integerPart = get_integer_part(number);
    char* fractionPart = get_fraction_part(number);

    // Get the binary representation of the integer part
    char* integerBinary = get_magnitude_binary(integerPart, REAL_PART_MAX_DIGITS);

    // Get the binary representation of the fraction part
    char* fractionBinary = fraction_to_binary(fractionPart, REAL_PART_MAX_DIGITS);

    // Extend integer and fraction binaries to BINARY_WORD_SIZE
    char* extendedInteger = extend_integer_binary(integerBinary, BINARY_WORD_SIZE);
    char* extendedFraction = extend_fraction_binary(fractionBinary, BINARY_WORD_SIZE);

    // Normalize the integer and fraction binaries and adjust the exponent
    int exponent = 0;
    normalize(extendedInteger, extendedFraction, &exponent);

    // Get the biased_127 binary representation of the exponent
    char* exponentBinary = int_to_biased_127(exponent);

    // Allocate memory for the floating-point string
    int bufferSize = FLOATING_POINT_MAX_DIGITS + 1; // Maximum digits + 1 for null terminator
    char* result = (char*)malloc(bufferSize);

    // Set the sign bit
    result[0] = (number[0] == '-') ? '1' : '0';

    // Copy the exponent binary into the floating point string
    strncpy(result + 1, exponentBinary, FLOATING_POINT_EXPONENT_DIGITS);

    // Copy the significand from the fraction binary into the floating point string
    strncpy(result + FLOATING_POINT_SIGNIFICAND_START, extendedFraction, FLOATING_POINT_SIGNIFICAND_DIGITS);

    return result;
}
```

