```cpp
#include <iostream>

using namespace std;

// Define a structure for the BST node
struct Node {
    int data;
    Node* left;
    Node* right;
};

// Function to create a new BST node
Node* createNode(int value) {
    Node* newNode = new Node;
    newNode->data = value;
    newNode->left = newNode->right = nullptr;
    return newNode;
}

// Function to insert a node into the BST
Node* insertNode(Node* root, int value) {
    if (root == nullptr) {
        return createNode(value);
    }
    if (value < root->data) {
        root->left = insertNode(root->left, value);
    }
    else if (value > root->data) {
        root->right = insertNode(root->right, value);
    }
    return root;
}

// Function to delete a node from the BST
Node* deleteNode(Node* root, int value) {
    // Implement the logic for deleting a node with various cases
    // Case 1: Node is a leaf node
    // Case 2: Node has one child
    // Case 3: Node has two children
    return root;
}

// Function to find the in-order successor of a node
Node* findInOrderSuccessor(Node* root, int value) {
    // Implement the logic for finding in-order successor with various cases
    // Case 1: Node is the largest in the tree
    // Case 2: Node has a right child
    // Case 3: Node doesn't have a right child
    return nullptr;
}

// Function to traverse the BST in-order
void inOrderTraversal(Node* root) {
    if (root == nullptr) {
        return;
    }
    inOrderTraversal(root->left);
    cout << root->data << " ";
    inOrderTraversal(root->right);
}

int main() {
    Node* root = nullptr;
    int choice, numNodes, data;

    cout << "Binary Search Tree Operations" << endl;

    do {
        cout << "1. Insert a node" << endl;
        cout << "2. Delete a node" << endl;
        cout << "3. Find in-order successor" << endl;
        cout << "4. Display in-order traversal" << endl;
        cout << "5. Exit" << endl;
        cout << "Enter your choice: ";
        cin >> choice;

        switch (choice) {
            case 1:
                cout << "Enter the number of nodes to insert: ";
                cin >> numNodes;
                for (int i = 0; i < numNodes; i++) {
                    cout << "Enter data for node " << i + 1 << ": ";
                    cin >> data;
                    root = insertNode(root, data);
                }
                break;
            case 2:
                cout << "Enter the data to delete: ";
                cin >> data;
                root = deleteNode(root, data);
                break;
            case 3:
                cout << "Enter the data to find in-order successor: ";
                cin >> data;
                Node* successor = findInOrderSuccessor(root, data);
                if (successor != nullptr) {
                    cout << "In-order successor: " << successor->data << endl;
                } else {
                    cout << "No in-order successor found." << endl;
                }
                break;
            case 4:
                cout << "In-order traversal: ";
                inOrderTraversal(root);
                cout << endl;
                break;
            case 5:
                cout << "Exiting the program." << endl;
                break;
            default:
                cout << "Invalid choice. Please enter a valid option." << endl;
        }
    } while (choice != 5);

    // Implement code to free memory and clean up the BST

    return 0;
}
