```c
void normalize(char* integer, char* fraction, int* exponent) {
    char* first_one = strchr(integer, '1');

    int index = 0;
    int fraction_one = 0;

    if (first_one == NULL)  {
        first_one = strchr(fraction, '1');
        if (first_one == NULL)  {
            // No '1' found in integer or fraction, nothing to normalize.
            return;
        }
        index = (int)(first_one - fraction);
        fraction_one = 1;
    } else  {
        index = (int)(first_one - integer);
    }

    if (fraction_one == 0) {
        *exponent = (int)(strlen(integer) - index - 1);
    } else {
        *exponent = (int)((index + 1) * -1);
    }

    if (*exponent >= 0)   {
        // Move the fraction part.
        sprintf(fraction, "%s%s", (first_one + 1), fraction);
    }

    if (*exponent < 0)   {
        // Adjust the exponent and zero out the integer part.
        sprintf(fraction, "%s", (first_one + 1));
        if (strlen(fraction) < BINARY_WORD_SIZE) {
            extend_fraction_binary(fraction, BINARY_WORD_SIZE);
        }

        // Zero out the integer part.
        memset(integer, '0', strlen(integer) - *exponent);
    }

    // Set the most significant bit in the integer part.
    integer[strlen(integer) - 1] = '1';
}
