body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    color: #5C5B6B;
    background-color: #F9DCC4;
  }

  .navbar {
    position: sticky;
    top: 0;
    background-color: #F7A278;
    box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
    z-index: 999;
  }

  .brand-logo {
    font-size: 1.5rem;
    font-weight: bold;
    color: #5C5B6B;
    text-decoration: none;
  }

  .navbar-menu {
    display: flex;
    list-style: none;
    margin: 0;
    padding: 0;
  }

  .navbar-item {
    margin-left: 1.5rem;
  }

  .navbar-item a {
    color: #5C5B6B;
    text-decoration: none;
  }

  .navbar-item a:hover {
    text-decoration: underline;
  }

  .container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 2rem;
    background-color: #FFFFFF;
    box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
    border-radius: 4px;
  }

  .container h1 {
    font-size: 3rem;
    margin-bottom: 1.5rem;
  }

  .container p {
    font-size: 1.25rem;
    line-height: 1.5;
    margin-bottom: 2rem;
  }

  .thumbnails {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
  }

  .thumbnail {
    width: 200px;
    height: 200px;
    object-fit: cover;
    margin: 0.5rem;
    border-radius: 4px;
    box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
  }








